export { type Player, type Coord, moveUp, moveDown, moveLeft, moveRight };
type Coord = { x: number; y: number}
type Player ={
    pos:Coord,
    HP:number,
}
const moveUp=(player:Player)=>{
    return {x:player.pos.x, y:player.pos.y-0.1}
}
const moveDown=(player:Player)=>{
